<script>
    document.addEventListener('DOMContentLoaded', function() {
        const modal = document.getElementById('cotizador-modal');
        const openBtn = document.getElementById('open-cotizador');
        const closeBtn = document.getElementById('close-cotizador');
        const form = document.getElementById('cotizador-form');
        const step1 = document.getElementById('step-1');
        const step2 = document.getElementById('step-2');
        const nextBtn = document.getElementById('next-to-step-2');
        const prevBtn = document.getElementById('prev-to-step-1');

        const tipoClienteRadios = form.querySelectorAll('input[name="tipo_cliente"]');
        const phoneContainer = document.getElementById('phone-field-container');
        const razonSocialContainer = document.getElementById('razon-social-field-container');
        const phoneInput = phoneContainer.querySelector('input');
        const razonSocialInput = razonSocialContainer.querySelector('input');

        function toggleFields() {
            const isEmpresa = form.querySelector('input[name="tipo_cliente"]:checked').value === 'Empresa';
            if (isEmpresa) {
                phoneContainer.classList.add('hidden');
                phoneInput.removeAttribute('required');
                razonSocialContainer.classList.remove('hidden');
                razonSocialInput.setAttribute('required', 'required');
            } else {
                phoneContainer.classList.remove('hidden');
                phoneInput.setAttribute('required', 'required');
                razonSocialContainer.classList.add('hidden');
                razonSocialInput.removeAttribute('required');
            }
        }

        tipoClienteRadios.forEach(radio => {
            radio.addEventListener('change', toggleFields);
        });

        // Initialize fields based on default checked radio
        toggleFields();

        // PRODUCT SELECTION LOGIC (Accordions & Tags)
        const productCategories = document.querySelectorAll('.product-category');
        const productCheckboxes = document.querySelectorAll('.product-checkbox');
        const tagsContainer = document.getElementById('selected-products-container');

        // Update Category Borders based on selection and expansion
        function updateCategoryBorders() {
            productCategories.forEach(category => {
                const checkboxes = category.querySelectorAll('.product-checkbox');
                const isAnyChecked = Array.from(checkboxes).some(cb => cb.checked);
                const isExpanded = category.getAttribute('data-expanded') === 'true';

                if (isAnyChecked || isExpanded) {
                    category.classList.add('border-black');
                    category.classList.remove('border-gray-200');
                } else {
                    category.classList.remove('border-black');
                    category.classList.add('border-gray-200');
                }
            });
        }

        // Toggle Accordions
        productCategories.forEach(category => {
            category.addEventListener('click', (e) => {
                // If the click was on the checkbox or its label, don't toggle the accordion
                if (e.target.closest('.category-content')) return;

                const content = category.querySelector('.category-content');
                const isExpanded = category.getAttribute('data-expanded') === 'true';

                // Close all other accordions first (optional but often better UX)
                productCategories.forEach(otherCat => {
                    if (otherCat !== category) {
                        otherCat.setAttribute('data-expanded', 'false');
                        otherCat.querySelector('.category-content').classList.add('hidden');
                    }
                });

                // Toggle this one
                if (isExpanded) {
                    category.setAttribute('data-expanded', 'false');
                    content.classList.add('hidden');
                } else {
                    category.setAttribute('data-expanded', 'true');
                    content.classList.remove('hidden');
                }

                // Update borders after expansion change
                updateCategoryBorders();
            });
        });

        // Update Tags
        function updateProductTags() {
            // Update Category Borders
            updateCategoryBorders();

            const selectedProducts = Array.from(productCheckboxes)
                .filter(cb => cb.checked)
                .map(cb => cb.value);

            // Clear container (except no-products message)
            const oldTags = tagsContainer.querySelectorAll('.product-tag');
            oldTags.forEach(tag => tag.remove());

            const noMsg = tagsContainer.querySelector('.no-products-msg');

            if (selectedProducts.length > 0) {
                noMsg.classList.add('hidden');

                selectedProducts.forEach(productName => {
                    const tag = document.createElement('div');

                    // El contenedor controla el hover con group
                    tag.className =
                        'product-tag btn btn-outline group flex items-center gap-2 ' +
                        'animation-fade-in animate-duration-200 !pr-4 ' +
                        'hover:bg-primary hover:border-primary transition-colors';

                    tag.innerHTML = `
                        <span class="text-gray-800 group-hover:text-white transition-colors text-sm">
                            ${productName}
                        </span>

                        <button
                            type="button"
                            class="remove-tag text-black 
                                   group-hover:!text-white 
                                   transition-colors bg-transparent border-none p-0 
                                   focus:outline-none"
                            data-value="${productName}"
                            aria-label="Eliminar ${productName}"
                        >
                            <svg
                                class="w-4 h-4"
                                fill="none"
                                stroke="currentColor"
                                viewBox="0 0 24 24"
                            >
                                <path
                                    stroke-linecap="round"
                                    stroke-linejoin="round"
                                    stroke-width="2"
                                    d="M6 18L18 6M6 6l12 12"
                                />
                            </svg>
                        </button>
                    `;

                    tagsContainer.appendChild(tag);
                });
            } else {
                noMsg.classList.remove('hidden');
            }
        }

        // Handle Checkbox Changes
        productCheckboxes.forEach(cb => {
            cb.addEventListener('change', updateProductTags);
        });

        // Handle Tag Removal
        tagsContainer.addEventListener('click', (e) => {
            const removeBtn = e.target.closest('.remove-tag');
            if (removeBtn) {
                const value = removeBtn.getAttribute('data-value');
                const checkbox = Array.from(productCheckboxes).find(cb => cb.value === value);
                if (checkbox) {
                    checkbox.checked = false;
                    updateProductTags();
                }
            }
        });

        // Toggle Modal
        openBtn.addEventListener('click', () => {
            modal.classList.remove('hidden');
            modal.classList.add('flex');
            document.body.style.overflow = 'hidden';
        });

        closeBtn.addEventListener('click', () => {
            modal.classList.add('hidden');
            modal.classList.remove('flex');
            document.body.style.overflow = '';
        });

        // Close on outside click
        modal.addEventListener('click', (e) => {
            if (e.target === modal) closeBtn.click();
        });

        // Step Navigation
        nextBtn.addEventListener('click', () => {
            // Simple validation for Step 1
            const required = form.querySelectorAll('#step-1 [required]');
            let valid = true;
            required.forEach(input => {
                if (!input.value) {
                    input.classList.add('border-red-500');
                    valid = false;
                } else {
                    input.classList.remove('border-red-500');
                }
            });

            if (valid) {
                step1.classList.add('hidden');
                step2.classList.remove('hidden');
                modal.scrollTo({
                    top: 0,
                    behavior: 'smooth'
                });
            }
        });

        prevBtn.addEventListener('click', () => {
            step2.classList.add('hidden');
            step1.classList.remove('hidden');
            modal.scrollTo({
                top: 0,
                behavior: 'smooth'
            });
        });

        // AJAX Submission
        form.addEventListener('submit', function(e) {
            e.preventDefault();

            const submitBtn = form.querySelector('[type="submit"]');
            const originalText = submitBtn.textContent;
            submitBtn.disabled = true;
            submitBtn.textContent = 'Enviando...';

            const formData = new FormData(form);
            formData.append('action', 'submit_cotizacion_comsatel');

            fetch('<?php echo admin_url('admin-ajax.php'); ?>', {
                    method: 'POST',
                    body: formData
                })
                .then(response => response.json())
                .then(data => {
                    if (data.success) {
                        window.location.href = '<?php echo home_url('/gracias'); ?>';
                    } else {
                        alert(data.data || 'Ocurrió un error al enviar el formulario.');
                        submitBtn.disabled = false;
                        submitBtn.textContent = originalText;
                    }
                })
                .catch(error => {
                    console.error('Error:', error);
                    alert('Error de conexión.');
                    submitBtn.disabled = false;
                    submitBtn.textContent = originalText;
                });
        });
    });
</script>